INSERT INTO public.skills (id, slug, title, description, category, levels, is_active, created_at) VALUES ('90a07ffd-c885-4d97-8522-7c017b18cf42'::uuid, 'syntactic_complexity', 'Sentence Structure', 'How flexibly you build clear, varied sentence structures so your ideas flow naturally (technical term: "syntactic complexity").', NULL, '[{"level": 1, "criteria": "Very short, simple SVO sentences; additive flow via simple coordinators.", "examples": ["I went to the store. I bought milk. I came home.", "The cat is black. It is big. I like it."], "rubricHints": ["Low average clause (T\u2011unit) length", "Frequent coordinating conjunctions (e.g., and)", "Minimal subordination"]}, {"level": 2, "criteria": "Uses a wider variety of coordinating conjunctions (e.g., but, so) to connect simple ideas.", "examples": ["I wanted to go, but it was raining, so I stayed inside.", "We finished the project, and we were happy with the results."], "rubricHints": ["Increased variety of coordinators", "Slightly higher average sentence length"]}, {"level": 3, "criteria": "Begins to use simple subordination (because, when); application may be formulaic.", "examples": ["I was late because the train was delayed.", "She cried when she heard the news."], "rubricHints": ["Presence of simple subordinating conjunctions (e.g., because, when)", "Low subordination index"]}, {"level": 4, "criteria": "Functional, consistent use of dependent clauses to add context and detail.", "examples": ["Since the meeting was cancelled, we went to lunch early.", "I decided to go to the park even though it was cloudy."], "rubricHints": ["Moderate use of subordination", "Occasional relative pronouns (who, which)"]}, {"level": 5, "criteria": "Good mix of simple, compound, and complex sentences; varied structure.", "examples": ["The report, which was released last week, includes several key findings we should discuss.", "Although the plan was rejected, we proposed an alternative that was accepted."], "rubricHints": ["Moderate structural variety", "Early relative clause usage"]}, {"level": 6, "criteria": "Frequent embedded clauses; increased information density.", "examples": ["The dog that chased the ball, which was red, ran into the street.", "I think the decision we made, though difficult, was the right one."], "rubricHints": ["Clear evidence of nested clauses", "Higher average dependency distance"]}, {"level": 7, "criteria": "Regular multi\u2011clause sentences; controls hierarchical relationships across ideas.", "examples": ["The committee\u2019s recommendation, responding to the unexpected shortfall, was tabled for review.", "The findings, though preliminary, strongly indicate the new treatment\u2019s advantage."], "rubricHints": ["High frequency of embeddings and participial phrases", "Clear thematic structuring"]}, {"level": 8, "criteria": "Employs nominalizations for abstraction and formal register.", "examples": ["The realization of the project\u2019s complexity led to a reevaluation of the timeline.", "The development of this policy is a functional necessity."], "rubricHints": ["Frequent nominalizations (\u2011tion, \u2011ment, etc.)", "More abstract style"]}, {"level": 9, "criteria": "Mastery of varied clause types; shifts forms strategically for rhetorical effect.", "examples": ["Why did the decision, ostensibly made to increase efficiency, reduce morale?", "Consider the implications: adopting this method could save millions."], "rubricHints": ["High variety of main clause types (declarative, interrogative, imperative)", "Advanced rhetorical structuring"]}, {"level": 10, "criteria": "Sophisticated structural mastery: nested clauses, strategic passives, nominalizations for precise discourse.", "examples": ["The proposed policy, predicated on careful macroeconomic analysis, should be given immediate consideration.", "Although early results were inconclusive, subsequent rigorous analysis provided definitive evidence."], "rubricHints": ["Consistently complex syntax for communicative goals", "High structural sophistication"]}]'::jsonb, TRUE, '2025-12-08T16:45:19');
INSERT INTO public.skills (id, slug, title, description, category, levels, is_active, created_at) VALUES ('cd0cb32c-9203-4fa5-9f89-a25c3485cfb8'::uuid, 'lexical_sophistication', 'Vocabulary', 'How broad, precise, and flexible your word choice is in different situations (technical term: "lexical sophistication").', NULL, '[{"level": 1, "criteria": "Very small, high\u2011frequency vocabulary; heavy repetition.", "examples": ["The house is big. It\u2019s a good house. I like the house.", "I like the stuff. It\u2019s good. I want it now."], "rubricHints": ["Low lexical diversity (e.g., TTR)", "High proportion of top\u20112k words", "Short average word length"]}, {"level": 2, "criteria": "Functional but basic vocabulary; avoids some repetition.", "examples": ["The building was large and impressive. We saw many things inside.", "It seems like a good idea and might be helpful."], "rubricHints": ["Low MTLD", "Moderate frequency of common words"]}, {"level": 3, "criteria": "Introduces simple synonyms to reduce repetition.", "examples": ["The plan was good, and the approach seemed sound.", "The structure was tall, and the design was impressive."], "rubricHints": ["Increased lexical diversity", "Early synonym substitution"]}, {"level": 4, "criteria": "Solid general\u2011purpose vocabulary with some descriptive range.", "examples": ["The conference was quite engaging, and I learned a great deal.", "The proposal is sound, though I have reservations."], "rubricHints": ["Moderate MTLD", "Beyond top\u2011frequency bands"]}, {"level": 5, "criteria": "Introduces domain\u2011specific terms appropriate to context.", "examples": ["We analyzed the dataset to identify anomalies.", "The system\u2019s extensibility is a key advantage."], "rubricHints": ["Domain\u2011specific vocabulary appears", "Higher content\u2011word usage"]}, {"level": 6, "criteria": "Prioritizes precise word choice; avoids vague generalities.", "examples": ["A dispassionate assessment supported the conclusion.", "Her meticulous planning improved reliability."], "rubricHints": ["High precision in selection", "Avoids polyfunctional verbs (make, have)"]}, {"level": 7, "criteria": "Effective figurative language and analogy.", "examples": ["We need to get to the heart of the issue.", "This project is the backbone of our roadmap."], "rubricHints": ["Presence of idioms and metaphors", "Rhetorical vividness"]}, {"level": 8, "criteria": "Comfortable with abstract and conceptual lexicon.", "examples": ["The confluence of these factors shifted the outcome.", "We must consider long\u2011term sustainability."], "rubricHints": ["High frequency of abstract nouns/verbs", "Higher MTLD"]}, {"level": 9, "criteria": "Consistently sophisticated and infrequent vocabulary; nuanced register.", "examples": ["The dichotomy between theory and practice persists.", "A congenial framework for subsequent iterations emerged."], "rubricHints": ["High vocd\u2011D", "Low frequency band usage"]}, {"level": 10, "criteria": "Mastery: precise, domain\u2011appropriate, and rhetorically impactful vocabulary.", "examples": ["This confluence precipitated a seismic shift in market equilibrium.", "Our nascent initiative burgeoned into a robust methodology."], "rubricHints": ["Consistently high MTLD/vocd\u2011D", "Expert use of infrequent and domain terms"]}]'::jsonb, TRUE, '2025-12-08T16:45:19');
INSERT INTO public.skills (id, slug, title, description, category, levels, is_active, created_at) VALUES ('b91ca9cf-93ae-4b11-a117-0847d4fd9a99'::uuid, 'discourse_coherence', 'Logic & Flow', 'How clearly your ideas are organized and connected so your listener can easily follow the thread (technical term: "discourse coherence").', NULL, '[{"level": 1, "criteria": "Disjointed ideas with no explicit links; listener must infer relationships.", "examples": ["I was tired. The movie was good. I ate popcorn.", "My dog is small. She likes to play. I went for a walk."], "rubricHints": ["No transitions or cohesive devices", "High semantic distance between sentences", "Minimal anaphora"]}, {"level": 2, "criteria": "Simple sequencing in time; basic chronological order.", "examples": ["I woke up. Then I went to work. After that, I came home.", "First we met, and then we reviewed the notes."], "rubricHints": ["Basic time\u2011based conjunctions"]}, {"level": 3, "criteria": "Introduces simple logical links (so, because, but).", "examples": ["I was tired, so I went to bed.", "The project was hard, but we finished on time."], "rubricHints": ["Coordinating conjunctions for cause/contrast"]}, {"level": 4, "criteria": "Consistent anaphora and referential clarity.", "examples": ["I like my new car. It is reliable and it saves gas.", "Our team worked hard. They delivered ahead of schedule."], "rubricHints": ["Well\u2011resolved pronoun references", "Clear linkbacks across sentences"]}, {"level": 5, "criteria": "Functional transitional phrases to guide the listener.", "examples": ["In addition, this approach reduces cost.", "For example, we sold fewer units in Q1."], "rubricHints": ["Presence of discourse markers (e.g., in addition, for example)"]}, {"level": 6, "criteria": "Signals complex semantic relations (concession, contrast, exemplification).", "examples": ["Nonetheless, the long\u2011term strategy remains sound.", "On the other hand, adoption has been slower than expected."], "rubricHints": ["Sophisticated cohesive devices for relations"]}, {"level": 7, "criteria": "Clear macro\u2011structure (intro/body/conclusion); framed segments.", "examples": ["First, I\u2019ll outline the problem; then the solution; finally, the timeline.", "Our argument proceeds in three steps: hypothesis, evidence, and implications."], "rubricHints": ["Enumeration and signposting"]}, {"level": 8, "criteria": "Coherence at sentence and paragraph level with multiple threads.", "examples": ["Our initial hypothesis was challenged; specifically, rising input costs altered viability.", "Efficiency is one issue; additionally, scalability affects long\u2011term success."], "rubricHints": ["High density and variety of cohesive devices", "Clear signaling of complex relations"]}, {"level": 9, "criteria": "Strategic management of given/new information to reduce cognitive load.", "examples": ["This challenge, as discussed, stems from outdated data.", "These results\u2014already noted\u2014support a revised approach."], "rubricHints": ["Effective information structure (given \u2192 new)"]}, {"level": 10, "criteria": "Flawless logical cohesion throughout a multi\u2011layered argument or narrative.", "examples": ["While the data suggests growth, countervailing behaviors temper our forecast; therefore, we proceed cautiously.", "First and foremost, reallocate resources; once done, the path forward is clear."], "rubricHints": ["Consistently high cohesive density", "Complex relation signaling and anaphora"]}]'::jsonb, TRUE, '2025-12-08T16:45:19');
INSERT INTO public.skills (id, slug, title, description, category, levels, is_active, created_at) VALUES ('e98c0d2d-a879-4746-b758-a97cad9bcb29'::uuid, 'rhetorical_effectiveness', 'Persuasion', 'How effectively you use examples, evidence, and tone to influence or move your listener (technical term: "rhetorical effectiveness").', NULL, '[{"level": 1, "criteria": "Descriptive/narrative only; no clear persuasive intent.", "examples": ["The policy changed. People were upset.", "It\u2019s a phone. It works for me."], "rubricHints": ["No explicit rhetorical moves", "Lack of argument markers"]}, {"level": 2, "criteria": "Simple assertion without justification.", "examples": ["We should adopt this software. It\u2019s the best.", "The project is important. We should prioritize it."], "rubricHints": ["Bare declarative claims", "No support"]}, {"level": 3, "criteria": "Basic justification with a single reason (because\u2026).", "examples": ["We should do this because it saves time.", "It\u2019s a bad idea because it\u2019s too expensive."], "rubricHints": ["Simple justification clauses"]}, {"level": 4, "criteria": "Introduces explicit evidence or references.", "examples": ["The report shows adoption is faster.", "According to the data, this reduces cost."], "rubricHints": ["Evidence markers (according to, data shows)"]}, {"level": 5, "criteria": "Clear thesis and logical progression from claim to evidence.", "examples": ["Our system is inefficient; adopting X will address bottlenecks.", "The team struggles; a new strategy can improve morale."], "rubricHints": ["Coherent argument structure", "Simple persuasive verbs"]}, {"level": 6, "criteria": "Begins to use ethos (credibility) and pathos (emotion).", "examples": ["As a veteran engineer, I\u2019ve seen this approach succeed.", "This decision affects local families; we must consider them."], "rubricHints": ["Personal expertise markers or emotional appeals"]}, {"level": 7, "criteria": "Uses refutation and/or concession to address counterpoints.", "examples": ["Some may argue it\u2019s costly, but long\u2011term benefits outweigh the initial spend.", "While competitors focus on short\u2011term gains, we prioritize sustainability."], "rubricHints": ["Refutation and concession markers"]}, {"level": 8, "criteria": "Employs complex moves (rhetorical questions, analogies, pointed examples).", "examples": ["If speed is our goal, why add steps?", "It\u2019s like fixing a leak by turning on the faucet."], "rubricHints": ["Rhetorical questions and analogies"]}, {"level": 9, "criteria": "Strategic framing to shape audience perception.", "examples": ["Invest now to avoid catastrophic failure later.", "We\u2019re not selling a product; we\u2019re offering a blueprint for success."], "rubricHints": ["Evaluative language and framing"]}, {"level": 10, "criteria": "Rhetorical mastery: multi\u2011faceted argument with ethos, pathos, logos in concert.", "examples": ["While short\u2011term costs rise, the data demonstrates superior long\u2011term returns, benefiting all stakeholders.", "We cannot disregard community needs; our shared future demands this path."], "rubricHints": ["Diverse advanced rhetorical moves", "Integrated emotional and logical appeals"]}]'::jsonb, TRUE, '2025-12-08T16:45:19');
INSERT INTO public.skills (id, slug, title, description, category, levels, is_active, created_at) VALUES ('92cb48b8-1f3d-4223-ba86-a4ee179403d6'::uuid, 'lexical_fluency', 'Smooth Delivery', 'How smoothly and confidently you speak\u2014reducing fillers and hesitations while using pauses for impact (technical term: "lexical fluency" / filler reduction).', NULL, '[{"level": 1, "criteria": "Heavily disfluent; frequent fillers and repetitions.", "examples": ["I, uh, think the, um, report was, like, good.", "So, I, uhm, went to the store and, uh, I got stuff."], "rubricHints": ["High filler rate per minute", "Frequent repetitions"]}, {"level": 2, "criteria": "Frequent fillers; message still recoverable but effortful.", "examples": ["The new, um, system is, uh, complex.", "We can, uh, start next week."], "rubricHints": ["High\u2013moderate filler rate"]}, {"level": 3, "criteria": "Moderate fillers; appear when searching for words or transitioning.", "examples": ["I believe we should, um, wait for the data.", "The plan is, uh, solid overall."], "rubricHints": ["Moderate filler rate"]}, {"level": 4, "criteria": "Occasional fillers; typically under cognitive load.", "examples": ["The implications are, um, significant.", "We need to, uh, consider all variables."], "rubricHints": ["Low filler rate"]}, {"level": 5, "criteria": "Minimal fillers; hesitations brief and non\u2011disruptive.", "examples": ["I propose a phased rollout with real\u2011time feedback.", "The analysis results are conclusive."], "rubricHints": ["Very low filler rate"]}, {"level": 6, "criteria": "Repetition used intentionally for emphasis.", "examples": ["This is a crucial\u2014crucial\u2014step.", "I am certain\u2014absolutely certain\u2014this will work."], "rubricHints": ["Purposeful repetition"]}, {"level": 7, "criteria": "Intentional unfilled pauses to aid processing and emphasis.", "examples": ["Our priority is quality \u2026 then speed.", "The solution, as I see it, is simple."], "rubricHints": ["Strategic, unfilled pauses"]}, {"level": 8, "criteria": "Highly fluid speech; near\u2011zero disfluencies.", "examples": ["The implications are clear; the path forward is unequivocal.", "Let\u2019s proceed with confidence and clarity."], "rubricHints": ["Near\u2011zero filler rate"]}, {"level": 9, "criteria": "Strategic pacing; varies rate and pauses to maintain attention.", "examples": ["This. Is. Not. Optional.", "We\u2019re ready \u2026 the technology is prepared \u2026 now we execute."], "rubricHints": ["Purposeful rhythm and pacing"]}, {"level": 10, "criteria": "Effortless fluency; entirely free of non\u2011purposeful hesitations.", "examples": ["The committee\u2019s unanimous decision advances our trajectory.", "Effective communication is clear, concise, and intentional."], "rubricHints": ["Zero or near\u2011zero fillers", "No self\u2011corrections or false starts"]}]'::jsonb, TRUE, '2025-12-08T16:45:19');
